import { useState, useEffect } from 'react';

export const useAnimalData = (apiPath) => {
    const [data, setData] = useState([]);
    const [searchResults, setSearchResults] = useState([]);
    const [error, setError] = useState("");
    const [searchError, setSearchError] = useState("");

    useEffect(() => {
        const fetchData = async () => {
            try {
                const url = `https://freetestapi.com/api/v1/${apiPath}?limit=6`;
                const response = await fetch(url);
                const data = await response.json();
                if (data.length === 0) {
                    setError("WE FOUND NOTHING FOR YOU! SEARCH SOMETHING ELSE");
                } else {
                    setError("");
                }
                setData(data);
            } catch (error) {
                console.error("An error occurred while fetching data.");
                setError("An error occurred while fetching data.");
            }
        };

        fetchData();

        return () => {
            // Cleanup function
            setData([]);
            setSearchResults([]);
            setError("");
            setSearchError("");
        };
    }, [apiPath]);

    const search = (query) => {
        if (!query) {
            setSearchResults([]);
            setSearchError("");
            return;
        }

        try {
            const results = data.filter(animal => animal.name.toLowerCase().includes(query.toLowerCase()));
            if (results.length === 0) {
                setSearchError("WE FOUND NOTHING FOR YOU! SEARCH SOMETHING ELSE");
            } else {
                setSearchError("");
            }
            setSearchResults(results);
        } catch (error) {
            console.error("An error occurred while searching.");
            setSearchError("An error occurred while searching.");
        }
    };

    return { data, searchResults, error, searchError, search };
};
