// AnimalForm.js
import React, { useState } from 'react';
import axios from 'axios';

export const AnimalForm = ({ type, onSuccess, closeModal }) => {
    const [formData, setFormData] = useState({
        name: '',
        origin: '',
        temperament: '',
        colors: '',
        description: '',
        image: '',
        // Additional fields specific to each animal type can be added here
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post(`http://localhost:5500/${type}/add`, formData);
            onSuccess();
            closeModal(); // Close modal after successful submission
        } catch (error) {
            console.error(`Error adding ${type}:`, error);
        }
    };

    return (
        <div className="fixed inset-0 overflow-y-auto z-50 flex justify-center items-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded-lg max-w-md w-full">
                <h2 className="text-2xl font-semibold mb-4">Add {type}</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <label>Name:</label>
                        <input type="text" name="name" value={formData.name} onChange={handleChange} className="w-full border border-gray-300 rounded-md p-2" />
                    </div>
                    {/* Add fields for other properties */}
                    <button type="submit" className="bg-green-500 text-white py-2 px-4 rounded hover:bg-green-700">Add</button>
                </form>
            </div>
        </div>
    );
};
