import { useState, useCallback } from 'react';

export const useSearch = (initialQuery = "") => {
    const [queryTerm, setQueryTerm] = useState(initialQuery);
    const [error, setError] = useState("");

    const handleSearch = (event) => {
        event.preventDefault();
        const query = event.target.search.value;
        if (query) {
            setQueryTerm(query);
            setError("");  // Clear error on new search
        }
        event.target.reset();
    };

    const handleReset = useCallback(() => {
        setQueryTerm("");
        setError("");  // Clear error on reset
    }, []);

    const setErrorState = useCallback((message) => {
        setError(message);
    }, []);

    return { queryTerm, handleSearch, handleReset, error, setErrorState };
};
