import { useState, useEffect } from 'react';
import { AnimalCard } from '../components';
import { useFetch } from '../hooks/useFetch';

export const AnimalList = ({ apiPath }) => {
    const [queryTerm, setQueryTerm] = useState("");
    const { data: animals, error, clearError } = useFetch(apiPath, queryTerm);

    useEffect(() => {
        // Reset the search term and clear any error message when the apiPath changes
        setQueryTerm("");
        clearError();
    }, [apiPath, clearError]);

    const handleSubmit = (event) => {
        event.preventDefault();
        const queryTerm = event.target.search.value;
        setQueryTerm(queryTerm);
        event.target.reset();
    };

    const handleReset = () => {
        setQueryTerm("");
    };

    return (
        <main>
            <section>
                <form onSubmit={handleSubmit} className="flex items-center">
                    <input
                        type="text"
                        id="search"
                        name="search"
                        className="block p-2 pl-10 w-full text-gray-900 bg-gray-50 rounded-lg border border-gray-300 sm:text-sm focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                        placeholder="Search..."
                        autoComplete="off"
                    />
                    <button type="submit" className="ml-2 p-2 bg-blue-500 text-white rounded">Search</button>
                    {queryTerm && (
                        <button type="button" onClick={handleReset} className="ml-2 p-2 bg-gray-500 text-white rounded">
                            Reset
                        </button>
                    )}
                </form>
            </section>
            <section className='max-w-7xl mx-auto py-7'>
                {error && <p className="text-red-500">{error}</p>}
                <div className='flex justify-start flex-wrap'>
                    {animals.map((animal) => (
                        <AnimalCard key={animal.id} apiPath={apiPath} animal={animal} />
                    ))}
                </div>
            </section>
        </main>
    );
};
