import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Admin = () => {
    const [dogs, setDogs] = useState([]);
    const [cats, setCats] = useState([]);
    const [birds, setBirds] = useState([]);

    useEffect(() => {
        fetchDogs();
        fetchCats();
        fetchBirds();
    }, []);

    const fetchDogs = async () => {
        try {
            const response = await axios.get('http://localhost:5000/dogs');
            setDogs(response.data);
        } catch (error) {
            console.error('Error fetching dogs:', error);
        }
    };

    const fetchCats = async () => {
        try {
            const response = await axios.get('http://localhost:5000/cats');
            setCats(response.data);
        } catch (error) {
            console.error('Error fetching cats:', error);
        }
    };

    const fetchBirds = async () => {
        try {
            const response = await axios.get('http://localhost:5000/birds');
            setBirds(response.data);
        } catch (error) {
            console.error('Error fetching birds:', error);
        }
    };

    const handleDelete = async (type, id) => {
        try {
            await axios.delete(`http://localhost:5000/${type}/${id}`);
            if (type === 'dogs') fetchDogs();
            if (type === 'cats') fetchCats();
            if (type === 'birds') fetchBirds();
        } catch (error) {
            console.error(`Error deleting ${type.slice(0, -1)}:`, error);
        }
    };

    const handleEdit = (type, id) => {
        // Implement the logic for editing an animal
        // This could involve navigating to another page or showing a modal with a form
    };

    const renderTable = (animals, type) => (
        <table>
            <thead>
                <tr>
                    {Object.keys(animals[0] || {}).map((key) => (
                        <th key={key}>{key}</th>
                    ))}
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {animals.map((animal) => (
                    <tr key={animal._id}>
                        {Object.keys(animal).map((key) => (
                            <td key={key}>{animal[key]}</td>
                        ))}
                        <td>
                            <button onClick={() => handleEdit(type, animal._id)}>Edit</button>
                            <button onClick={() => handleDelete(type, animal._id)}>Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );

    return (
        <div>
            <h1>Admin</h1>
            <h2>Dogs</h2>
            {renderTable(dogs, 'dogs')}
            <h2>Cats</h2>
            {renderTable(cats, 'cats')}
            <h2>Birds</h2>
            {renderTable(birds, 'birds')}
        </div>
    );
};

export default Admin;
