import React, { useState } from 'react';
import axios from 'axios';

const AnimalForm = ({ type, fetchAnimals }) => {
    const [newAnimalData, setNewAnimalData] = useState({
        name: '',
        origin: '',
        temperament: '',
        description: '',
        image: '',
        species: '',
        family: '',
        habitat: '',
        place_of_found: '',
        diet: '',
        wingspan_cm: '',
        weight_kg: '',
    });

    const handleAddAnimal = async (event) => {
        event.preventDefault();

        try {
            await axios.post(`http://localhost:5500/${type}`, newAnimalData);
            setNewAnimalData({
                name: '',
                origin: '',
                temperament: '',
                description: '',
                image: '',
                species: '',
                family: '',
                habitat: '',
                place_of_found: '',
                diet: '',
                wingspan_cm: '',
                weight_kg: '',
            });
            fetchAnimals();
        } catch (error) {
            console.error(`Error adding ${type.slice(0, -1)}:`, error);
        }
    };

    return (
        <div className="mb-6">
            <h2 className="text-2xl font-semibold mb-4">Add New {type.slice(0, -1)}</h2>
            <form onSubmit={handleAddAnimal} className="space-y-4">
                {Object.keys(newAnimalData).map((key) => (
                    <div key={key}>
                        <label className="block text-sm font-medium text-gray-700">{key}</label>
                        <input
                            type="text"
                            value={newAnimalData[key]}
                            onChange={(e) => setNewAnimalData({ ...newAnimalData, [key]: e.target.value })}
                            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        />
                    </div>
                ))}
                <div>
                    <button type="submit" className="bg-green-500 text-white py-2 px-4 rounded hover:bg-green-700">Add</button>
                </div>
            </form>
        </div>
    );
};

export default AnimalForm;
