// AnimalForm.js
import React, { useState } from 'react';
import axios from 'axios';

const AnimalForm = ({ type, onSuccess }) => {
    const [formData, setFormData] = useState({
        name: '',
        origin: '',
        temperament: '',
        colors: [],
        description: '',
        image: '',
        // Additional fields specific to each animal type can be added here
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post(`http://localhost:5500/${type}/add`, formData);
            onSuccess();
        } catch (error) {
            console.error(`Error adding ${type}:`, error);
        }
    };

    return (
        <div>
            <h2>Add {type}</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} />
                </div>
                <div>
                    <label>Origin:</label>
                    <input type="text" name="origin" value={formData.origin} onChange={handleChange} />
                </div>
                <div>
                    <label>Temperament:</label>
                    <input type="text" name="temperament" value={formData.temperament} onChange={handleChange} />
                </div>
                <div>
                    <label>Colors:</label>
                    <input type="text" name="colors" value={formData.colors} onChange={handleChange} />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea name="description" value={formData.description} onChange={handleChange} />
                </div>
                <div>
                    <label>Image:</label>
                    <input type="text" name="image" value={formData.image} onChange={handleChange} />
                </div>
                <button type="submit">Add</button>
            </form>
        </div>
    );
};

export default AnimalForm;
