import { useEffect, useState } from 'react';
import { AnimalCard } from '../components';
import { useAnimalData } from '../hooks/useAnimalData';

export const AnimalList = ({ apiPath }) => {
    const { data, searchResults, error, searchError, search } = useAnimalData(apiPath);
    const [queryTerm, setQueryTerm] = useState("");

    useEffect(() => {
        // Perform initial search when data changes
        search("");
    }, [data, search]);

    const displayedAnimals = queryTerm ? searchResults : data;
    const displayedError = queryTerm ? searchError : error;

    const handleSearch = (event) => {
        event.preventDefault();
        const query = event.target.search.value;
        setQueryTerm(query);
        search(query);
    };

    return (
        <main>
            <section>
                <form onSubmit={handleSearch} className="flex items-center">
                    <input
                        type="text"
                        id="search"
                        name="search"
                        className="block p-2 pl-10 w-full text-gray-900 bg-gray-50 rounded-lg border border-gray-300 sm:text-sm focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                        placeholder="Search..."
                        autoComplete="off"
                    />
                    <button type="submit" className="ml-2 p-2 bg-blue-500 text-white rounded">Search</button>
                    {queryTerm && (
                        <button type="button" onClick={() => setQueryTerm("")} className="ml-2 p-2 bg-gray-500 text-white rounded">
                            Reset
                        </button>
                    )}
                </form>
            </section>
            <section className='max-w-7xl mx-auto py-7'>
                {displayedError && <p className="text-red-500">{displayedError}</p>}
                <div className='flex justify-start flex-wrap'>
                    {displayedAnimals.map((animal) => (
                        <AnimalCard key={animal.id} apiPath={apiPath} animal={animal} />
                    ))}
                </div>
            </section>
        </main>
    );
};
