import { AnimalCard } from '../components';
import { useFetch, searchAnimal } from '../hooks/useFetch';
import {SearchBar} from '../components/SearchBar';
import { useState  } from 'react';
export const AnimalList = ({apiPath}) => {

  const {data : animals} = useFetch(apiPath);

  const [animal, setAnimals] = useState(initialAnimals);

  const handleSearch = async (apiPath, name) => {
    const searchedAnimals = await searchAnimal(apiPath, name);
    setAnimals(searchedAnimals);
    setSearched(true);
  };

  return (
    <main>
       <SearchBar apiPath={apiPath} onSearch={handleSearch} />

      <section className='max-w-7xl mx-auto py-7'>
        <div className='flex justify-start flex-wrap'>
          {animals.map((animal) => (
            <AnimalCard key={animal.id} apiPath={apiPath} animal={animal} />
          ))} 
        </div>
      </section>
    </main>
  );
};
